import React, { useState } from 'react';
import SectionHeading from './SectionHeading';
import Slider from 'react-slick';

export default function Testimonial() {
  const [testimonials, setTestimonials] = useState([]);
  const [formData, setFormData] = useState({
    avatarImg: '',
    reviewText: '',
    avatarName: '',
    avatarCompany: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setTestimonials((prev) => [...prev, formData]);
    setFormData({
      avatarImg: '',
      reviewText: '',
      avatarName: '',
      avatarCompany: '',
    });
  };

  var settings = {
    dots: true,
    arrows: false,
    infinite: true,
    autoplay: false,
    autoplaySpeed: 4000,
    speed: 1000,
    slidesToShow: 1,
    slidesToScroll: 1,
    initialSlide: 0,
  };

  return (
    <section className="section effect-section pb-0">
      <div className="effect-3">
        <img src="/images/effect-3.svg" alt="" />
      </div>
      <div className="effect-4">
        <img src="/images/effect-4.svg" alt="" />
      </div>
      <div className="container">
        <SectionHeading
          miniTitle="Testimonials"
          title="What our clients say"
          variant="text-center"
        />
        <div data-aos="fade" data-aos-duration="1200" data-aos-delay="300">
          <Slider {...settings}>
            {testimonials.map((item, index) => (
              <div key={index}>
                <div className="testimonial-box">
                  <div className="t-user">
                    <img src={item.avatarImg} alt="Avatar" />
                  </div>
                  <div className="t-text">{item.reviewText}</div>
                  <div className="t-person">
                    <h6>{item.avatarName}</h6>
                    <span>{item.avatarCompany}</span>
                  </div>
                </div>
              </div>
            ))}
          </Slider>
        </div>
        <div className="form-container">
          <h3>Add a Testimonial</h3>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              name="avatarImg"
              placeholder="Avatar Image URL"
              value={formData.avatarImg}
              onChange={handleChange}
              required
            />
            <textarea
              name="reviewText"
              placeholder="Review Text"
              value={formData.reviewText}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="avatarName"
              placeholder="Name"
              value={formData.avatarName}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="avatarCompany"
              placeholder="Company"
              value={formData.avatarCompany}
              onChange={handleChange}
              required
            />
            <button type="submit">Add Testimonial</button>
          </form>
        </div>
      </div>
    </section>
  );
}
